import React, { useState, useEffect } from "react";
import "./App.css";
import Navbar from "./components/Navbar/Navbar"; // Adjust path if necessary

function App() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");
  const [error, setError] = useState(null);

  //For Edit Functionality
  const [editTaskId, setEditTaskId] = useState(null);
const [editDescription, setEditDescription] = useState('');

  useEffect(() => {
    fetch("http://localhost:1433/api/tasks")
      .then((response) => response.json())
      .then((data) => {
        console.log("Response data:", data); // Add logging to see the response

        setTodos(data); // Assuming the API returns an array of tasks
      })
      .catch((error) => {
        console.error("Error fetching tasks:", error);
        setError("Failed to fetch tasks."); // Set error state
      });
  }, []);

  const addTask = () => {
    if (task.trim() === "") {
      setError("Task description cannot be empty.");
      return;
    }

    fetch("http://localhost:1433/api/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ description: task }),
    })
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(text);
          });
        }
        return response.json();
      })
      .then((data) => {
        console.log("Response data:", data); // Add logging to see the response

        if (data.taskId) {
          // Assuming the backend returns taskId as a numeric value
          setTodos([...todos, { TaskId: data.taskId, Description: task }]);
          setTask("");
          setError(null);
        } else {
          setError("Task ID not returned from server.");
        }
      })
      .catch((error) => {
        console.error("Error adding task:", error);
        setError("Failed to add task: " + error.message); // Show detailed error
      });
  };

  const deleteTask = (taskId) => {
    fetch(`http://localhost:1433/api/tasks/${taskId}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (!response.ok) {
          return response.text().then((text) => {
            throw new Error(text);
          });
        }
        return response.json();
      })
      .then((data) => {
        if (data.message) {
          // Refresh tasks after deletion
          fetch("http://localhost:1433/api/tasks")
            .then((response) => response.json())
            .then((data) => {
              setTodos(data); // Refresh the list of tasks
              setError(null);
            })
            .catch((error) => {
              console.error("Error fetching tasks after deletion:", error);
              setError("Failed to fetch tasks after deletion.");
            });
        }
      })
      .catch((error) => {
        console.error("Error deleting task:", error);
        setError("Failed to delete task: " + error.message); // Show detailed error
      });
  };

  const editTask = (des,id) => {
    alert(`${des},${id}`);
    // fetch('apirequest/',{params})
    fetch(`http://localhost:1433/api/tasks/:${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({des:task}),
    })
      .then((response) => {
        if (!response.ok)
          return response.text().then((text) => {
            throw new Error(text);
          });
        return response.json();
      })
      .then(data => {
        console.log("Task updated successfully:",data)
      });
  };

  return (
    <>
      <Navbar />
      <div className="App">
        <header className="App-header">
          <h1>ToDo List</h1>
          <div className="task-input">
            <input
              type="text"
              value={task}
              onChange={(e) => setTask(e.target.value)}
              placeholder="Enter task description"
            />
            <button onClick={addTask}>Add Task</button>
          </div>
          {error && <p className="error">{error}</p>}
          <table className="task-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Description</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {todos.length > 0 ? (
                todos.map((todo) => (
                  <tr key={todo.TaskId}>
                    <td>{todo.TaskId}</td>
                    <td>{editTaskId === todo.TaskId
                    ?(<input type="text" value={editDescription}
                    onChange={(e) => setEditDescription(e.target.value)}
                    placeholder="Enter new description">Save</input>
                    ):(todo.Description)}</td>
                    <td>
                      <button onClick={() => deleteTask(todo.TaskId)}>
                        Delete
                      </button>
                    </td>
                    <td>
                      {/* <button onClick={() => alert(`Task Description: ${todo.Description}\nTask ID: ${todo.TaskId}`)}>Edit</button> */}
                      {editTaskId === todo.TaskId
                      ?<button onClick={editTask}>Save</button>
                      :(<button onClick={() => 
                        {
                          setEditTaskId(todo.TaskId);
                          setEditDescription(todo.Description);
                        }
                        // editTask(todo.Description, todo.TaskId)
                        }>
                        Edit
                      </button>)}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3">No tasks available</td>
                </tr>
              )}
            </tbody>
          </table>
        </header>
      </div>
    </>
  );
}

export default App;
