import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar/Navbar'; // Adjust path if necessary

function App() {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState('');
  const [error, setError] = useState(null);

  // Function to fetch tasks from the server
  const fetchTasks = () => {
    fetch('http://localhost:1433/api/tasks')
      .then(response => response.json())
      .then(data => {
        console.log('Response data:', data); // Add logging to see the response
        setTodos(data); // Assuming the API returns an array of tasks
      })
      .catch(error => {
        console.error('Error fetching tasks:', error);
        setError('Failed to fetch tasks.'); // Set error state
      });
  };

  // Initial fetch on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = () => {
    if (task.trim() === '') {
      setError('Task description cannot be empty.');
      return;
    }

    fetch('http://localhost:1433/api/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ description: task }),
    })
      .then(response => {
        if (!response.ok) {
          return response.text().then(text => {
            throw new Error(text);
          });
        }
        return response.json();
      })
      .then(data => {
        console.log('Response data:', data); // Add logging to see the response

        if (data.taskId) {
          // Assuming the backend returns taskId as a numeric value
          setTodos([...todos, { Id: data.taskId, Description: task }]);
          setTask('');
          setError(null);
        } else {
          setError('Task ID not returned from server.');
        }
      })
      .catch(error => {
        console.error('Error adding task:', error);
        setError('Failed to add task: ' + error.message); // Show detailed error
      });
  };

  const deleteTask = (taskId) => {
    fetch(`http://localhost:1433/api/tasks/${taskId}`, {
      method: 'DELETE',
    })
      .then(response => {
        if (!response.ok) {
          return response.text().then(text => {
            throw new Error(text);
          });
        }
        return response.json();
      })
      .then(data => {
        if (data.message) {
          // Optimistically update UI
          setTodos(todos.filter(todo => todo.Id !== taskId));
          setError(null);
        }
      })
      .catch(error => {
        console.error('Error deleting task:', error);
        setError('Failed to delete task: ' + error.message); // Show detailed error
      });
  };

  // Handle refreshing tasks
  const handleRefresh = () => {
    fetchTasks();
  };

  return (
    <>
      <Navbar />
      <div className="App">
        <header className="App-header">
          <h1>ToDo List</h1>
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            placeholder="Enter task description"
          />
          <button onClick={addTask}>Add Task</button>
          <button onClick={handleRefresh}>Refresh Tasks</button>
          {error && <p className="error">{error}</p>}
          <ul>
            {todos.length > 0 ? (
              todos.map(todo => (
                <li key={todo.Id}>
                  {todo.Description}
                  <button onClick={() => deleteTask(todo.Id)}>Delete</button>
                </li>
              ))
            ) : (
              <p>No tasks available</p>
            )}
          </ul>
        </header>
      </div>
    </>
  );
}

export default App;
